sports-exercise.map:
function (doc) {
    var res_map = {
      'cricket' :0,
      'tennis' : 0,
      'footy' : 0,
      'motorsport' : 0,
      'soccer' : 0,
      'exercise' : 0
    };
    var sports_words = {
      'cricket' : ["cricket", "batsman", "bowler", "t20", "odi", "mcg", "scg", "bbl", "bigbash"],
      'tennis' : ["tennis", "australianopen", "nadal", "djokovic", "federer", "kyrgios", "barty", "atp", "williams"],
      'footy' : ["afl", "footy","aussierules","collingwoodfc","richmond_fc","sydneyswans"],
      'motorsport' : ["ferrari","vettel","Lewis","prix","ricciardo","formula1","f1","ausgp","motogp", "motorsport"],
      'soccer' : ["fifa", "aleague","soccer","liverpool","messi", "ronaldo", "epl", "mufc", "ffa", "melcity", "melbournevictory"],
      'exercise' : ["exercise","workout","gym","yoga","jogging","aerobics","cardio"]
    }

    var tokens = doc.full_text.match(/\w+/g).map((s) => s.toLowerCase());
    for(var sport_name in sports_words){
        for(key in sports_words[sport_name]){
          if(tokens.includes(sports_words[sport_name][key])){
              res_map[sport_name] += 1;
              // log(sports_words[sport_name][key] +", "+ doc.full_text)
          }
        }
    }
    var emit_res=[];
    for(var sport_name in res_map){
      emit_res.push(res_map[sport_name]);
    }
    if(sum(emit_res)){
      emit(doc.sa2_2016_lv12_code, emit_res);
    }

}
